sysauto_script.script:
// Get today's time and date
	var rightNow = new GlideDateTime();

	// Consultar o banco de dados para registros de Tarefa NeedIt com valores de campo de data de vencimento mais antigos
	// do que a hora atual. Retorne apenas registros de Tarefa NeedIt que não tenham
	// um valor do campo State de Closed Complete.
	var overdueNITasks = new GlideRecord('x_58872_needit_needit_task');
	overdueNITasks.addQuery('due_date','<=',rightNow);
	overdueNITasks.addQuery('state','!=',3);

	overdueNITasks.query();
	// Escreva uma mensagem de log para cada Registro de Tarefa NeedIt vencido
	while(overdueNITasks.next()){
		gs.info("Overdue NeedIt Task record = " + overdueNITasks.number);
		gs.eventQueue('x_58872_needit.overdueNeedItTask',overdueNITasks,overdueNITasks.number,gs.getUserName());
	}
