sysauto_script.script:
//Número de registros NeedIt para criar
	var recordsToMake = 500;

	// Declare e inicialize as variáveis ​​usadas no script
	var genNIRec;
	var i;
	var tableName = "x_58872_needit_needit";
	var requestTypeArray = ["facilities","hr","legal"];
	var selectRequestType = "";
	var selectWhatNeeded = 3;
	var pickDate = 1;
	var requestedForArray = [{"name":"Fred Luddy","email":"fred.luddy@example.com"},{"name":"Beth Anglin","email":"beth.anglin@example.com"},{"name":"System Administrator","email":"admin@example.com"}];
	var pickUser = 10;


	//For loop para criar registros
		for (i = 0; i < recordsToMake; i++) {

			//Crie um registro vazio para a tabela NeedIt
			genNIRec = new GlideRecord(tableName);
			genNIRec.newRecord();


			//Selecione um valor aleatório da matriz requestType e defina o valor do campo Tipo de Solicitação (opção)
			selectRequestType = requestTypeArray[(Math.floor(Math.random() * 3))];
			genNIRec.setValue("u_request_type",selectRequestType);


			//Defina o valor do campo O que é necessário (escolha) em Tipo de solicitação e 1 ou 2
			selectWhatNeeded = (Math.floor(Math.random()*2) +1);		
			genNIRec.setValue("u_what_needed",selectRequestType + selectWhatNeeded);

			//Defina o valor do campo de descrição resumida (string)
			genNIRec.setValue("short_description", "Automatically created NeedIt request: " + selectRequestType + ", " + selectRequestType + selectWhatNeeded);

			//Defina o valor do campo Quando necessário (data / hora) para uma data entre seis meses a partir de agora e seis meses atrás
			var rightNow = new GlideDateTime();		
			pickDate = (Math.floor(Math.random() * 366) - 182);
			rightNow.addDaysUTC(pickDate);		
			genNIRec.setValue("u_when_needed",rightNow.getDate());


			//Defina o valor do campo Requested for (referência), selecionando um usuário aleatório de requestForArray.
			//Defina também o valor do campo Requested for email (string). O campo de referência não pode ser definido em um aplicativo com escopo
			//usando o método setValue, a menos que use sys_id para definir o valor do campo. Para definir por nome de exibição, use
			//o método GlideElement setDisplayValue. gr. <field_name> .setDisplayValue (<value>).
			pickUser = (Math.floor(Math.random()*3));
			genNIRec.u_requested_for.setDisplayValue(requestedForArray[pickUser].name);
			genNIRec.setValue("u_requested_for_email",requestedForArray[pickUser].email.toString());


			//Defina o valor do campo Prioridade (escolha) para um valor aleatório
			genNIRec.setValue("priority",(Math.floor(Math.random()*5)+1));

			//Defina o valor do campo Estado (opção) como Solicitado
			genNIRec.setValue("state",13);

			//Insira o novo registro NeedIt na tabela
			genNIRec.insert();
	}
