sysauto_script.script:
// Recupere o valor da propriedade do aplicativo x_58872_needit.autoCloseOverdue
	var howLongToClose = gs.getProperty('x_58872_needit.autoCloseOverdue');

	// Consultar o banco de dados para registros de Tarefa Necessária com campo Data de vencimento
	// valores anteriores ao número de dias definido em x_58872_needit.autoCloseOverdue
	// Propriedade do aplicativo. Apenas os registros de tarefa NeedIt de retorno têm um valor de estado de
	// Aberto ou Trabalho em andamento. Feche as Tarefas NeedIt e atualize
	// as notas do NeedIt Task Work.
	var overdueNITasks = new GlideRecord('x_58872_needit_needit_task');
	overdueNITasks.addQuery('due_date','<=',gs.daysAgo(howLongToClose));
	overdueNITasks.addQuery('state','<',3);

	overdueNITasks.query();
	// Grava uma mensagem de log para cada Registro de Tarefa NeedIt muito atrasado.  
	// Adicione uma nota ao campo Notas de trabalho. Altere o estado para Fechado Incompleto.
	// Atualize o registro da Tarefa NeedIt.
	while(overdueNITasks.next()){
		gs.info("Auto close NeedIt Task record = " + overdueNITasks.number);
		overdueNITasks.work_notes = " Registro fechado porque estava muito além da data de vencimento..";
		overdueNITasks.state = 4;
		overdueNITasks.update();
	}
